            const result = forecastPortfolioGrowth(10000, 500, 7, 10);
            // FV(initial) = 10000 * (1 + 0.07/12)^120 = 20096.61
            // FV(contributions) = 500 * ((1 + 0.07/12)^120 - 1) / (0.07/12) = 86542.40
